export default function HabitBox({ color, name, count, symbol }) {
    return (
        <View style={[styles.container]}>
            <Pressable style={[{ backgroundColor: color }]}>
                <View style={styles.ellipsisContainer}>
                    <View style={[styles.ellipsisContainer]}>
                        <Text style={[styles.habitName, { fontSize: 25 }]}>
                            {count}
                        </Text>
                        <Ionicons name="flame" size={30} color="#fff" />
                    </View>
                    <Ellipsis onPress={null} />
                </View>
                <View style={styles.textContainer}>
                    <Text style={styles.habitName}>{name}</Text>
                </View>
            </Pressable>
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        borderRadius: 10,
        overflow: "hidden",
        minWidth: 200,
        padding: 1,
    },

    ellipsisContainer: {
        paddingTop: 5,
        flexDirection: "row",
        justifyContent: "space-between",
        alignContent: "center", //Den er vist ikke nødvendig -Jeppe
        alignItems: "center",
        padding: 10,
    },

    textContainer: {
        flexDirection: "row",
        flex: 1,
        alignItems: "flex-end",
        padding: 10,
    },

    habitName: {
        color: "#fff",
        fontSize: 25,
    },
});
