import { Button, SafeAreaView, StyleSheet, View } from "react-native";
import { useState } from "react";
import HabitPage from "./components/HabitPage";

export default function App() {
  const [safeAreaDimensions, setSafeAreaDimensions] = useState({
    x: 1,
    y: 1,
    width: 1,
    height: 1,
  });
  const handleSafeAreaLayout = (event) => {
    const { x, y, width, height } = event.nativeEvent.layout;
    setSafeAreaDimensions({ x, y, width, height });
  };

  return (
    <View style={{ flex: 1 }}>
      <SafeAreaView
        style={{ flex: 1 }}
        onLayout={handleSafeAreaLayout}
      ></SafeAreaView>

      <View
        style={{
          position: "absolute",
          justifyContent: "center",
          width: "100%",
          height: "100%",
        }}
      >
        <Button
          title={"log"}
          onPress={() => console.log(safeAreaDimensions)}
        ></Button>
      </View>
      <View
        style={{
          position: "absolute",
          width: safeAreaDimensions.width - 40,
          height: safeAreaDimensions.height - 40,
          top: 20,
          bottom: 20,
          backgroundColor: "pink",
        }}
      ></View>
    </View>
  );
}

const styles = StyleSheet.create({});
