import {
  Button,
  SafeAreaView,
  StyleSheet,
  View,
  Text,
  Dimensions,
} from "react-native";
import { useState } from "react";
import HabitPage from "./components/HabitPage";
import LogButton from "./components/LogButton";

export default function App() {
  const [safeAreaDimensions, setSafeAreaDimensions] = useState({
    x: 1,
    y: 1,
    width: 1,
    height: 1,
  });
  const handleSafeAreaLayout = (event) => {
    const { x, y, width, height } = event.nativeEvent.layout;
    setSafeAreaDimensions({ x, y, width, height });
    console.log("Height", height, "StartY", y);
  };

  return (
    <View style={{ flex: 1 }}>
      <SafeAreaView
        style={{ flex: 1 }}
        onLayout={(event) => {
          const { x, y, width, height } = event.nativeEvent.layout;
          console.log("LAYOUT", height);
        }}
      >
        <View
          style={{ flex: 1, backgroundColor: "tomato" }}
          onLayout={handleSafeAreaLayout}
        >
          <Text>hi</Text>
        </View>
      </SafeAreaView>
      <View
        style={{
          position: "absolute",
          width: safeAreaDimensions.width,
          height: safeAreaDimensions.height,
          top: Dimensions.get("window").height - safeAreaDimensions.y,

          backgroundColor: "pink",
          opacity: 0.6,
        }}
      ></View>

      {/*This button can log whatever */}
      <LogButton log={"hello world"}></LogButton>
    </View>
  );
}

const styles = StyleSheet.create({});
